import jsonimport randomimport stringimport osvoucher_file = "voucher_codes.json"# Fungsi untuk membaca kode voucherdef read_vouchers():    if os.path.exists(voucher_file):        with open(voucher_file, 'r') as file:            return json.load(file)    return {}# Fungsi untuk menyimpan kode voucherdef save_vouchers(vouchers):    with open(voucher_file, 'w') as file:        json.dump(vouchers, file)# Fungsi untuk generate kode voucherdef generate_voucher_code(length=25):    return ''.join(random.choices(string.ascii_uppercase + string.digits, k=length))# Fungsi untuk membuat voucher barudef create_voucher():    vouchers = read_vouchers()    new_code = generate_voucher_code()    vouchers[new_code] = True  # True berarti masih bisa digunakan    save_vouchers(vouchers)    print(f" Voucher Baru: {new_code}")# Fungsi untuk menghapus semua voucherdef clear_vouchers():    save_vouchers({})    print(" Semua voucher telah dihapus.")# Pilihan menuif __name__ == "__main__":    print("1. Generate Voucher Baru")    print("2. Hapus Semua Voucher")    pilihan = input("Pilih (1/2): ")    if pilihan == "1":        create_voucher()    elif pilihan == "2":        clear_vouchers()    else:        print(" Pilihan tidak valid.")